import axios from ' axios'


export const SIGNIN_USER = "SIGNIN_USER";




export const getUser = () => {
    return (dispatch) => {
      return axios
        .get(`http://localhost:3000/users`)
        .then((res) => {
          dispatch({ type: SIGNIN_USER, payload: res.data });
        })
        .catch((err) => console.log(err));
    };
  };
