import axios from "axios";


export const SIGNIN_USER = "SIGNIN_USER";
export const ACCES_PROFIL = "ACCES_PROFIL";


export const signInUser = (userMail, password) => {
  return (dispatch) => {
    return axios({
      method: "post",
      url: `http://localhost:3001/api/v1/user/login`,
      data: {
        email: userMail,
        password: password,
      },
    })
      .then((response) => {
       console.log(response)
       if (response.ok){
      localStorage.setItem("token" , "Bearer " + response.data.token )

       }

      //  dispatch({ type: SIGNIN_USER, payload: res.data });
      })
      .catch((err) => console.log(err));
  };
};





export const accessProfilePage = (token) => {
  return (dispatch) => {
    return axios({
      method: "post",
      url: `http://localhost:3001/api/v1/user/profile`,
     headers: {
       Authorization: token
     }
    })
      .then((res) => {
        dispatch({ type: ACCES_PROFIL, payload: res.Authorization });
      })
      .catch((err) => console.log(err));
  };
};
