import axios from "axios";


export const SIGNIN_USER = "SIGNIN_USER";
export const ACCES_PROFIL = "ACCES_PROFIL";


export const signInUser = (userMail, password) => {
  return (dispatch) => {
    return axios({
      method: "post",
      url: `http://localhost:3001/api/v1/user/login`,
      data: {
        email: userMail,
        password: password,
      },
    })
      .then((response) => {
       console.log(response)
       if (response.status === 200){
         console.log('ok')
      //localStorage.setItem("token" , response.data.body.token )

       }

      dispatch({ type: SIGNIN_USER, payload: localStorage.setItem("token" , response.data.body.token )});
      })
      .catch((err) => console.log(err));
  };
};





export const accessProfilePage = () => {
  return (dispatch) => {
    return axios({
      method: "post",
      url: `http://localhost:3001/api/v1/user/profile`,
     headers: {
       Authorization: "Bearer" + localStorage.getItem('token')
     }
    })
      .then((response) => {
       dispatch({ type: ACCES_PROFIL, payload: response.Authorization });
       console.log(response)
      })
      .catch((err) => console.log(err));
  };
};
